public class ForLoopNaturalNumber {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int sum=0;
		
		
		for(int a=1; a<=7;a++) {
			sum = sum + a;
		}
		System.out.println(sum);
	}

}
